import { useState, useEffect } from 'react';
import './SongsPage.css';

const dummySongs = [
    {
        id: '1',
        name: 'Blinding Lights',
        artist: 'The Weeknd',
        uri: 'spotify:track:0VjIjW4GlUZAMYd2vXMi3b',
        imageUrl:
            'https://i.scdn.co/image/ab67616d0000b2738863bc11d2aa12b54f5aeb36',
    },
    {
        id: '2',
        name: 'Levitating',
        artist: 'Dua Lipa',
        uri: 'spotify:track:463CkQjx2Zk1yXoBuierM9',
        imageUrl:
            'data:image/jpeg;base64,',
    },
    {
        id: '3',
        name: 'Shape of You',
        artist: 'Ed Sheeran',
        uri: 'spotify:track:7qiZfU4dY1lWllzX7mPBI3',
        imageUrl:
            'data:image/jpeg;base64,',
    },
];

const dummyPlaylists = [
    {
        id: '1',
        name: 'Workout Mix',
        description: 'Perfect for your workout session',
        songs: [],
        imageUrl:
            'data:image/jpeg;base64,',
    },
    {
        id: '2',
        name: 'Chill Vibes',
        description: 'Relaxing tunes for your downtime',
        songs: [],
        imageUrl:
            'data:image/jpeg;base64,',
    },
    {
        id: '3',
        name: 'Party Time',
        description: 'Get the party started',
        songs: [],
        imageUrl:
            'data:image/jpeg;base64,',
    },
];

interface Song {
    id: string;
    name: string;
    artist: string;
    uri?: string;
    imageUrl?: string;
}

interface Playlist {
    id: string;
    name: string;
    description: string;
    songs: Song[];
    imageUrl?: string;
}

const SongsPage = () => {
    const [playlists, setPlaylists] = useState<Playlist[]>(dummyPlaylists);
    const [selectedPlaylist, setSelectedPlaylist] = useState<Playlist | null>(
        null
    );
    const [removingSongId, setRemovingSongId] = useState<string | null>(null);

    useEffect(() => {
        if (selectedPlaylist) {
            const updatedPlaylist = playlists.find(
                (p) => p.id === selectedPlaylist.id
            );
            if (updatedPlaylist) {
                setSelectedPlaylist(updatedPlaylist);
            }
        }
    }, [playlists]);

    const handleAddToPlaylist = (song: Song, playlistId: string) => {
        setPlaylists(
            playlists.map((playlist) => {
                if (
                    playlist.id === playlistId &&
                    !playlist.songs.find((s) => s.id === song.id)
                ) {
                    return {
                        ...playlist,
                        songs: [...playlist.songs, song],
                    };
                }
                return playlist;
            })
        );
    };

    const handleRemoveFromPlaylist = (songId: string, playlistId: string) => {
        setRemovingSongId(songId);

        setTimeout(() => {
            const updatedPlaylists = playlists.map((playlist) => {
                if (playlist.id === playlistId) {
                    return {
                        ...playlist,
                        songs: playlist.songs.filter(
                            (song) => song.id !== songId
                        ),
                    };
                }
                return playlist;
            });
            setPlaylists(updatedPlaylists);
            setRemovingSongId(null);
        }, 300);
    };

    return (
        <div className='songs-page-container'>
            <div className='song-main-content'>
                <div className='songs-section'>
                    <h2 className='section-title'>Discover Songs</h2>
                    <div className='songs-grid'>
                        {dummySongs.map((song) => (
                            <div key={song.id} className='song-card'>
                                <div className='song-image'>
                                    <img src={song.imageUrl} alt={song.name} />
                                </div>
                                <div className='song-info'>
                                    <h3 className='song-name'>{song.name}</h3>
                                    <p className='song-artist'>{song.artist}</p>
                                    {selectedPlaylist && (
                                        <button
                                            className='add-to-playlist-btn'
                                            onClick={() =>
                                                handleAddToPlaylist(
                                                    song,
                                                    selectedPlaylist.id
                                                )
                                            }>
                                            Add to {selectedPlaylist.name}
                                        </button>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                <div className='playlists-section'>
                    <h2 className='section-title'>My Playlists</h2>
                    <div className='playlists-list'>
                        {playlists.map((playlist) => (
                            <div
                                key={playlist.id}
                                className={`playlist-item ${
                                    selectedPlaylist?.id === playlist.id
                                        ? 'selected'
                                        : ''
                                }`}
                                onClick={() => setSelectedPlaylist(playlist)}>
                                <div className='playlist-header'>
                                    <img
                                        src={playlist.imageUrl}
                                        alt={playlist.name}
                                        className='playlist-image'
                                    />
                                    <div className='playlist-info'>
                                        <h3>{playlist.name}</h3>
                                        <p>{playlist.description}</p>
                                        <span className='song-count'>
                                            {playlist.songs.length} songs
                                        </span>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {selectedPlaylist && (
                        <div className='selected-playlist-songs'>
                            <h3 className='sub-title'>
                                Songs in {selectedPlaylist.name}
                            </h3>
                            <div className='playlist-songs-list'>
                                {selectedPlaylist.songs.length === 0 ? (
                                    <div className='empty-playlist'>
                                        <p>No songs in this playlist yet</p>
                                        <p className='sub-text'>
                                            Add songs from the discover section
                                        </p>
                                    </div>
                                ) : (
                                    selectedPlaylist.songs.map(
                                        (song, index) => (
                                            <div
                                                key={song.id}
                                                className={`playlist-song-item ${
                                                    removingSongId === song.id
                                                        ? 'removing'
                                                        : ''
                                                }`}>
                                                <div className='song-number'>
                                                    {index + 1}
                                                </div>
                                                <img
                                                    src={song.imageUrl}
                                                    alt={song.name}
                                                    className='playlist-song-image'
                                                />
                                                <div className='playlist-song-info'>
                                                    <h4>{song.name}</h4>
                                                    <p>{song.artist}</p>
                                                </div>
                                                <button
                                                    className='remove-song-btn'
                                                    onClick={(e) => {
                                                        e.stopPropagation();
                                                        handleRemoveFromPlaylist(
                                                            song.id,
                                                            selectedPlaylist.id
                                                        );
                                                    }}>
                                                    Remove
                                                </button>
                                            </div>
                                        )
                                    )
                                )}
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default SongsPage;
